import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [deferredPrompt, setDeferredPrompt] = useState(null);
  const [isAppInstalled, setIsAppInstalled] = useState(false);
  const [showInstallModal, setShowInstallModal] = useState(false);

  useEffect(() => {
    // Verifica se o app já está instalado
    const isInstalled = window.matchMedia('(display-mode: standalone)').matches || window.navigator.standalone;
    setIsAppInstalled(isInstalled);

    // Evento para capturar o prompt de instalação
    const handleBeforeInstallPrompt = (e) => {
      e.preventDefault();
      setDeferredPrompt(e);

      // Mostra o modal automaticamente se não estiver instalado
      if (!isInstalled) {
        setShowInstallModal(true);
      }
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    window.addEventListener('appinstalled', () => {
      setIsAppInstalled(true);
      setDeferredPrompt(null);
      setShowInstallModal(false);
    });

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      window.removeEventListener('appinstalled', () => {});
    };
  }, []);

  const handleInstallClick = () => {
    if (deferredPrompt) {
      deferredPrompt.prompt();
      deferredPrompt.userChoice.then((choiceResult) => {
        if (choiceResult.outcome === 'accepted') {
          console.log('Usuário aceitou a instalação');
        } else {
          console.log('Usuário recusou a instalação');
        }
        setDeferredPrompt(null);
        setShowInstallModal(false);
      });
    }
  };

  const closeModal = () => {
    setShowInstallModal(false);
  };

  return (
    <div className="app-container">
      {/* Seu conteúdo existente */}
      <h1>Hello World PWA!</h1>
      <p>Bem-vindo ao meu primeiro PWA elaborado com React.</p>

      {/* Modal de instalação */}
      {showInstallModal && (
        <div className="install-modal">
          <div className="modal-content">
            <h2>Instalar App</h2>
            <p>Deseja instalar este aplicativo em seu dispositivo para uma melhor experiência?</p>
            <div className="modal-buttons">
              <button onClick={closeModal} className="secondary-button">
                Agora não
              </button>
              <button onClick={handleInstallClick} className="primary-button">
                Instalar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
