import { useEffect } from 'react';
import './App.css';

let deferredPrompt;

function App() {
  useEffect(() => {
    // Verifica se já está instalado
    const isInstalled = window.matchMedia('(display-mode: standalone)').matches || 
                       window.navigator.standalone;

    if (!isInstalled) {
      window.addEventListener('beforeinstallprompt', (e) => {
        // Impede o prompt padrão do navegador
        e.preventDefault();
        // Armazena o evento para mostrar depois
        deferredPrompt = e;

        // Mostra o prompt automaticamente após pequeno delay
        setTimeout(() => {
          if (deferredPrompt) {
            deferredPrompt.prompt();

            // Aguarda a resposta do usuário
            deferredPrompt.userChoice.then((choiceResult) => {
              if (choiceResult.outcome === 'accepted') {
                console.log('Usuário aceitou a instalação');
              } else {
                console.log('Usuário recusou a instalação');
              }
              deferredPrompt = null;
            });
          }
        }, 1500); // Delay de 1.5 segundos para melhor UX
      });
    }

    // Detecta se foi instalado
    window.addEventListener('appinstalled', () => {
      console.log('PWA foi instalado');
      deferredPrompt = null;
    });

    return () => {
      window.removeEventListener('beforeinstallprompt', () => {});
      window.removeEventListener('appinstalled', () => {});
    };
  }, []);

  return (
    <div className="app-container">
      <h1>Meu PWA Incrível</h1>
      <p>Bem-vindo ao nosso aplicativo progressivo!</p>
    </div>
  );
}

export default App;
